import { sha256 } from '../_tools/password-hash';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "./session-storage.service";
export class AuthService {
    constructor(httpClient, sessionStorage) {
        this.httpClient = httpClient;
        this.sessionStorage = sessionStorage;
        this.AUTH_SERVER = "http://127.0.0.1:5400";
    }
    register(user) {
        return this.httpClient.post(this.AUTH_SERVER + "/api/user", user);
    }
    getAll() {
        return this.httpClient.get(this.AUTH_SERVER + "/api/user");
    }
    getByEmail(mail) {
        return this.httpClient.get(this.AUTH_SERVER + "/api/user/" + mail);
    }
    async login(mail, pass) {
        pass = await sha256(pass).finally(() => {
            this.getByEmail(mail).toPromise().then(resp => {
                if (resp.Password == pass) {
                    this.sessionStorage.set('user', JSON.stringify(resp));
                }
            });
        });
    }
    logout() {
        this.sessionStorage.delete('user');
    }
    isAuthenticated() {
        if (this.sessionStorage.get('user') !== null)
            return true;
        return false;
    }
    getUser() {
        return this.sessionStorage.get('user');
    }
    setUser(user) {
        this.sessionStorage.set('user', user);
    }
}
