        this.authService = authService;
        this.router = router;
        this.type = 0;
        this.selected = null;
        this.properties = [];
    }
    ngOnInit() {
        this.propertyService.getAll().then(data => {
            data.forEach(element => {
                if (element.Attente === true)
                    this.properties.push(element);
            });
        });
    }
    async validate(id) {
        let prop = this.properties[id];
        prop.Attente = false;
        this.propertyService.validateProperty(prop);
        window.location.reload();
    }
    async del(id) {
        let prop = this.properties[id];
        prop.Attente = false;
        this.propertyService.deleteProperty(prop);
        window.location.reload();
    }
    async profile(id) {
        this.selected = this.properties[id];
    }
    async reset() {
        this.selected = null;
    }
}
