        this.httpClient = httpClient;
        this.sessionStorage = sessionStorage;
        this.AUTH_SERVER = "http://127.0.0.1:5400";
        this.isLogged = new BehaviorSubject(false);
    }
    register(user) {
        return this.httpClient.post(this.AUTH_SERVER + "/api/user", user);
    }
    async login(user) {
        await sha256(user.Password).then(() => {
            return this.httpClient.post(this.AUTH_SERVER + "/api/login", user);
        });
    }
    getAll() {
        return this.httpClient.get(this.AUTH_SERVER + "/api/user");
    }
    getByEmail(mail) {
        return this.httpClient.get(this.AUTH_SERVER + "/api/user/" + mail);
    }
    logout() {
        this.isLogged.next(false);
        this.sessionStorage.delete('user');
        this.sessionStorage.delete('accessToken');
    }
    isAuthenticated() {
        return this.isLogged.value;
    }
    getUser() {
        return this.sessionStorage.get('user');
    }
    setUser(user) {
        this.sessionStorage.set('user', user);
    }
}
