import { BehaviorSubject } from 'rxjs';
import { sha256 } from '../_tools/password-hash';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "./session-storage.service";
export class AuthService {
    constructor(httpClient, sessionStorage) {
        this.httpClient = httpClient;
        this.sessionStorage = sessionStorage;
        this.AUTH_SERVER = "http://127.0.0.1:5400";
        this.isLogged = new BehaviorSubject(false);
        this.actualToken = null;
        this.actualUser = null;
    }
    register(user) {
        return this.httpClient.post(this.AUTH_SERVER + "/api/user", user);
    }
    getAll() {
        return this.httpClient.get(this.AUTH_SERVER + "/api/user");
    }
    getByEmail(mail) {
        return this.httpClient.get(this.AUTH_SERVER + "/api/user/" + mail);
    }
    getToken(mail, pass) {
        return this.httpClient.post(this.AUTH_SERVER + "/api/login", { Email: mail, Password: pass });
    }
    async login(form) {
        form.value.Password = await sha256(form.value.Password).finally(() => {
            this.getByEmail(form.value.Email).toPromise().then(resp => {
                if (resp.Password == form.value.Password) {
                    this.getToken(form.value.Email, form.value.Password).toPromise().then((r) => {
                        this.setStoredToken(JSON.stringify(r));
                        this.actualToken = r;
                        console.log(actualToken);
                    });
                    this.setUser(JSON.stringify(resp));
                    this.actualUser = resp;
                    this.isLogged.next(true);
                }
            });
        });
    }
    logout() {
        this.isLogged.next(false);
        this.sessionStorage.delete('user');
        this.sessionStorage.delete('accessToken');
        this.actualUser = null;
        this.actualToken = null;
    }
    isAuthenticated() {
        return this.isLogged.value;
    }
    getUser() {
        return this.sessionStorage.get('user');
    }
    setUser(user) {
        this.sessionStorage.set('user', user);
    }
    getStoredToken() {
        return this.sessionStorage.get('accessToken');
    }
    setStoredToken(accessToken) {
        this.sessionStorage.set('accessToken', accessToken);
    }
}
