import { tap } from 'rxjs/operators';
import { BehaviorSubject } from 'rxjs';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "@angular/router";
export class AuthService {
    constructor(httpClient, router) {
        this.httpClient = httpClient;
        this.router = router;
        this.AUTH_SERVER = "http://127.0.0.1:8000";
        this.authSubject = new BehaviorSubject(false);
    }
    //permet d'inscrire un nouveau utilisateur
    register(user) {
        return this.httpClient.post(`${this.AUTH_SERVER}/api/register`, user).pipe(tap((res) => {
            //verification du token
            //console.log(res);
            localStorage.setItem("ACCESS_TOKEN", res.token);
            localStorage.setItem("EXPIRES_IN", res.token);
            this.authSubject.next(true);
        }));
    }
    //permet la connection d'un utilisateur
    singIn(user) {
        return this.httpClient.post(`${this.AUTH_SERVER}/api/login_check`, user).pipe(tap(async (res) => {
            //verification du token
            //console.log(res);
            localStorage.setItem("ACCESS_TOKEN", res.token);
            //localStorage.setItem("EXPIRES_IN", res.user.expires_in);
            this.authSubject.next(true);
        }));
    }
    //permet la disconnection de l'utilisateur
    signOut() {
        localStorage.removeItem("ACCESS_TOKEN");
        //localStorage.removeItem("EXPIRES_IN");
        this.authSubject.next(false);
        this.router.navigateByUrl('connection');
    }
    //Returning the authSubject as Observable
    isAuthenticated() {
        if (localStorage.getItem("ACCESS_TOKEN") != null) {
            return true;
        }
        else {
            return false;
        }
    }
}
